$( document ).ready(function() {  $.get('/qa',function(data){    var data = JSON.parse(data);    var i = 0;    showTime();    setTimeout(function() {      firstBlockFadeIn();      setTimeout(function(){        $('.reply-btn').removeClass('delay-show');        $('.reply-btn').removeClass('hidden');      },1000);    },1000);        $('.reply-btn').click(function(){      firstDialog();    });    function showTime() {      var d = new Date();      var h = d.getHours();      var m = d.getMinutes();      var s = d.getSeconds();      var time = h + (m < 10 ? ":0" : ":") + m + (s < 10 ? ":0" : ":") + s;      $('.time').text(time);    }    function firstBlockFadeIn() {      $('.meejia-block').addClass('animate-block');    }    function firstDialog() {      var btn_value = $('.reply-btn').text();      var question = data[i][0];      var answer = data[i][1];      var dataLength = data.length;      var question_last = data[dataLength - 1][0];      var answer_last = data[dataLength - 1][1];      if (btn_value == "我有hin多美国买房问题！！！") {        $('.my-answer').text(btn_value);        $('.my-block').addClass('animate-block');        $('.reply-btn').addClass('hidden');        var right_block_animate = $('.my-block').hasClass('animate-block');        if (right_block_animate) {          setTimeout(function() {            $('.content').append("<div class='left-block'><div class='meejia-block block animate-block'><div class='meejia-avatar'></div><div class='meejia-answer answer'>那还等什么？开始吧！</div></div></div>");            setTimeout(function(){              $('.reply-btn').text(question).removeClass('hidden');            },2000);          }, 2000);        }      } else {        $('.reply-btn').addClass('hidden');        setTimeout(function(){          $('.content').append("<div class='right-block'><div class='my-block block animate-block'><div class='my-answer answer'>" + question + "</div></div></div>");          $(".content").animate({ scrollTop: $('.content').prop("scrollHeight")}, 1000);          setTimeout(function(){            $('.content').append("<div class='left-block'><div class='meejia-block block animate-block'><div class='meejia-avatar'></div><div class='meejia-answer answer'>" + answer + "</div></div></div>");            $(".content").animate({ scrollTop: $('.content').prop("scrollHeight")}, 1000);          },2000);          i++;          if ( i < dataLength) {            var new_question = data[i][0];            setTimeout(function(){              $('.reply-btn').text(new_question).removeClass('hidden');            },2000);          } else {            $('.reply-btn').remove();            setTimeout(function(){              $('.content').append("<div class='left-block'><div class='meejia-block block animate-block'><div class='meejia-avatar'></div><div class='meejia-answer answer no-lineheight'><img src='http://api.meejia.cn/shared_qr/login.png' class='answer-img'></div></div></div>");              setTimeout(function(){                $('.auto-reply').append("<div class='btn-like'>想要问更多问题请长按扫描二维码！</div>");              },2000);            },2000);          }        },2000);      }    }  });});